import React, { useState } from 'react';
import { Controlled as CodeMirror } from 'react-codemirror2';
import 'codemirror/lib/codemirror.css'; // Core CSS for CodeMirror
import 'codemirror/theme/material.css'; // Material theme for CodeMirror
import 'codemirror/mode/python/python'; // Python syntax highlighting
import { diffChars } from 'diff'; // Correct: Named import

const CodeEditor = ({ code, setCode, handleRun, handleQuickFix }) => {
  const [originalCode, setOriginalCode] = useState(code);

  // Function to calculate and display differences between original and updated code
  const applyDiff = (original, updated) => {
    const diff = diffChars(original, updated); // Use diffChars directly
    return diff.map((part, index) => {
      const style = {
        color: part.added ? 'green' : part.removed ? 'red' : 'black',
        fontWeight: part.added || part.removed ? 'bold' : 'normal',
      };
      return <span key={index} style={style}>{part.value}</span>;
    });
  };

  return (
    <div className="flex-1 p-4">
      {/* CodeMirror Editor */}
      <CodeMirror
        value={code}
        onBeforeChange={(editor, data, value) => setCode(value)}
        options={{
          mode: 'python', // Set the language mode to Python
          theme: 'material', // Use the Material theme
          lineNumbers: true, // Show line numbers
        }}
      />

      {/* Buttons for Running Code and Applying Quick Fix */}
      <div className="mt-4 flex gap-4">
        <button
          onClick={handleRun}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          Run Code
        </button>
        <button
          onClick={() => {
            setOriginalCode(code); // Save the current code before applying quick fix
            handleQuickFix();
          }}
          className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
        >
          Quick Fix
        </button>
      </div>

      {/* Display Changes Between Original and Updated Code */}
      <div className="mt-4">
        <h3 className="text-lg font-bold">Changes:</h3>
        <div>{applyDiff(originalCode, code)}</div>
      </div>
    </div>
  );
};

export default CodeEditor;
